import React from 'react';
import { Bar } from 'react-chartjs-2';

// import { fetchDailyData } from '../../api';

import styles from './Chart.module.css';

const Comparitivechart = ({ data1: { confirmed1, recovered1, deaths1 }, country1 }, { data2: { confirmed2, recovered2, deaths2 }, country2 }) => {
    // const [dailyData, setDailyData] = useState({});

    // useEffect(() => {
    //   const fetchMyAPI = async () => {
    //     const initialDailyData = await fetchDailyData();

    //     setDailyData(initialDailyData);
    //   };

    //   fetchMyAPI();
    // }, []);

    console.log(confirmed1, recovered1, deaths1);

    const barChart = (
        confirmed1 ? (
          <Bar
            data={{
              labels: ['country1', 'country2'],
              datasets: [
                {
                  label: 'People',
                  backgroundColor: ['rgba(0, 0, 255, 0.5)', 'rgba(0, 255, 0, 0.5)', 'rgba(255, 0, 0, 0.5)'],
                  data: [confirmed1.value, confirmed2.value],
                },
              ],
            }}
            options={{
              legend: { display: false },
              title: { display: true, text: `Current state in ${country1}` },
            }}
          />
        ) : null
      );
      return (
        <div className={styles.container}>
          {barChart }
        </div>
      );
    };



export default Comparitivechart;
