import React from 'react';
import { Bar } from 'react-chartjs-2';

// import { fetchData } from '../../api';

import styles from './Comparitivechart.module.css';

const Comparitivechart = ({ data: { confirmed1, recovered1, deaths1 }, country, 
    CompData: { confirmed2, recovered2, deaths2 }, CompCountry }) => {


    console.log(CompCountry);

    console.log(confirmed2);

    const barChart = (
        confirmed1 ? (
          <Bar
            data={{
              labels: [country, CompCountry],
              datasets: [
                {
                  label: 'People',
                  backgroundColor: ['rgba(0, 0, 255, 0.5)', 'rgba(0, 255, 0, 0.5)'],
                  data: [confirmed1.value, confirmed1.value],
                },
              ],
            }}
            options={{
              legend: { display: false },
              title: { display: true, text: `Current state in ${country}` },
            }}
          />
        ) : null
      );
      return (
        <div className={styles.container}>
          {barChart }
        </div>
      );
    };



export default Comparitivechart;
