    console.log(Data);
    const barChart = (
        confirmed ? (
          <Bar
            data={{
              labels: ['country', 'country2'],
              datasets: [
                {
                  label: 'People',
                  backgroundColor: ['rgba(0, 0, 255, 0.5)', 'rgba(0, 255, 0, 0.5)'],
                  data: [confirmed.value, recovered.value],
                },
              ],
            }}
            options={{
              legend: { display: false },
              title: { display: true, text: `Current state in ${country}` },
            }}
          />
        ) : null
      );
      return (
        <div className={styles.container}>
          {barChart }
        </div>
      );
    };



export default Comparitivechart;
